const ADMIN_TOKEN=localStorage.getItem("auth_token");if(!ADMIN_TOKEN){window.location.href="login.html"}const hostURL=window.location.origin;console.log(hostURL);async function fetchStickers(){const hostEndpoint=hostURL+"/api/stickers";const response=await fetch(hostEndpoint,{headers:{Authorization:`Bearer ${ADMIN_TOKEN}`}});return await response.json()}async function deleteSticker(id){await fetch(`/stickers/${id}`,{method:"DELETE",headers:{Authorization:`Bearer ${ADMIN_TOKEN}`}});location.reload()}function showStickerModal(sticker){console.log(sticker);console.log("running showStickerModal");const modal=document.getElementById("stickerModal");const modalImage=document.getElementById("modalStickerImage");const modalUsers=document.getElementById("modalUserList");const modalStickerId=document.getElementById("modalStickerId");modalImage.src=sticker.file_path;modalStickerId.value=sticker.sticker_id;modalUsers.innerHTML="";sticker.users.forEach((user=>{const userItem=document.createElement("div");userItem.className="list-group-item";userItem.innerHTML=`\n            <div class="row align-items-center">\n                <div class="col">\n                    <div class="text-body">${user.telegram_user}</div>\n                    <div class="text-muted">${user.telegram_id}</div>\n                </div>\n            </div>\n        `;modalUsers.appendChild(userItem)}));modal.classList.remove("fade");modal.classList.add("show");modal.style.display="block";modal.removeAttribute("aria-hidden");modal.setAttribute("aria-modal","true");modal.setAttribute("role","dialog")}function hideStickerModal(){const modal=document.getElementById("stickerModal");modal.removeAttribute("aria-modal");modal.classList.remove("show");modal.classList.add("fade");modal.style.display="none"}document.addEventListener("DOMContentLoaded",(function(){const closeButton=document.querySelector("#stickerModal .btn-close");if(closeButton){closeButton.addEventListener("click",hideStickerModal)}const modal=document.getElementById("stickerModal");modal.addEventListener("click",(function(event){if(event.target===modal){hideStickerModal()}}));document.addEventListener("keydown",(function(event){if(event.key==="Escape"&&modal.classList.contains("show")){hideStickerModal()}}))}));document.getElementById("deleteStickerBtn").addEventListener("click",(async()=>{const stickerId=document.getElementById("modalStickerId").value;if(confirm("Are you sure you want to delete this sticker?")){await deleteSticker(stickerId);const modal=document.getElementById("stickerModal");modal.removeAttribute("data-show");await loadStickers()}}));async function loadStickers(){const stickers=await fetchStickers();const activeContainer=document.getElementById("sticker_active");const inactiveContainer=document.getElementById("sticker_inactive");activeContainer.innerHTML="";inactiveContainer.innerHTML="";const stickerGroups={};stickers.forEach((sticker=>{if(!stickerGroups[sticker.sticker_id]){stickerGroups[sticker.sticker_id]={sticker_id:sticker.sticker_id,file_path:sticker.file_path,enabled:sticker.enabled,users:[]};const usergroup=sticker.telegram;usergroup.forEach((sticker_telegram=>{stickerGroups[sticker.sticker_id].users.push({telegram_user:sticker_telegram.user,telegram_id:sticker_telegram.id})}))}}));Object.values(stickerGroups).forEach((stickerGroup=>{const container=stickerGroup.enabled?activeContainer:inactiveContainer;const img=document.createElement("img");img.className="sticker";img.src=stickerGroup.file_path;img.alt="Sticker";img.onclick=()=>showStickerModal(stickerGroup);container.appendChild(img)}))}async function clearAll(){if(confirm("Are you sure you want to clear ALL stickers?")){const stickers=await fetchStickers();for(const id of stickers){await deleteSticker(id)}location.reload()}}function showTab(tab){document.getElementById("stickersTab").style.display=tab==="stickers"?"block":"none";document.getElementById("configTab").style.display=tab==="config"?"block":"none"}loadStickers();